mapper.insert:

	select seq_tagboard.nextval from dual

insert into tagboard(
	bo_no, bo_title, bo_content, bo_writer, bo_date, bo_hit
) values (
	#{boNo}, #{boTitle}, #{boContent}, #{boWriter}, sysdate, 0
)

insert into tag(
	bo_no, tag
) values (
	#{boNo}, #{tagName}
)

mapper.update:
update tagboard
set
	bo_title = #{boTitle},
	bo_content = #{boContent}
where bo_no = #{boNo}
